(function($){$(".js-toggle-menu").click(function(e){e.preventDefault();$(".nav--mobile").toggleClass("open");if($(this).text()=="Menu"){$(this).text("Close")}else{$(this).text("Menu")}});$(".js-toggle-search").click(function(e){e.preventDefault();$(".overlay--search, html, body").toggleClass("open")});$(".has-child-menu a").click(function(e){e.preventDefault();if($(this).next(".child-menu").hasClass("open")){$(this).closest(".has-child-menu").removeClass("open");$(this).next(".child-menu").removeClass("open")}else{$(".child-menu, .has-child-menu").removeClass("open");$(this).closest(".has-child-menu").toggleClass("open");$(this).next(".child-menu").toggleClass("open")}})})(jQuery);